package com.act.activiti.service.impl;import com.act.activiti.dao.MembershipDao;import com.act.activiti.entity.Membership;import com.act.activiti.service.MembershipService;import org.springframework.stereotype.Service;import javax.annotation.Resource;/** * @author pen */@Servicepublic class MembershipServiceImpl implements MembershipService {	@Resource	MembershipDao membershipDao;		@Override	public Membership find(String userId) {		return membershipDao.find(userId);	}}